import React, {useEffect, useState} from 'react'
import * as fx from 'money'
import classNames from 'classnames'
import {getCurrenciesForMoneyJs} from "api";

const CalcPage = () => {
    const [currenciesRates, setCurrenciesRates] = useState({})
    const [value, setValue] = useState('')
    const [result, setResult] = useState('')
    const [error, setError] = useState('')
// console.log('currenciesRates', currenciesRates)

    const text = 'Input your values'

    const classes = classNames({
        "form-label": true,
        "text-danger": error
    })

    const handleSubmit = (event) => {
        event.preventDefault()
        try {
            const valueToArr = value.split(' ')
            // console.log('valueToArr', valueToArr)
            const currValue = valueToArr[0]
            const currSimbolFrom = valueToArr[1].toUpperCase()
            const currSimbolTo = valueToArr[3].toUpperCase()
            fx.base = currenciesRates.base
            fx.rates = currenciesRates.rates
            const result = (fx(currValue).from(currSimbolFrom).to(currSimbolTo)).toFixed(2)
            // console.log('result', result)
            setResult(result)
        } catch (e) {
            setError('You made a mistake! Enter the valid data')
        }
    }

    useEffect(async () => {
        const curr = await getCurrenciesForMoneyJs()
        setCurrenciesRates(curr)
    }, [getCurrenciesForMoneyJs])

    useEffect(() => {
        setError('')
    }, [value])

    return (
        <div className='container'>
            <div className='row'>
                <div
                    className='col-md-6 col-xs-12 shadow p-3 mb-5 bg-white rounded'
                    style={{"padding": "15px", "marginTop": "8rem"}}
                >
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="exampleInputEmail1" className={classes}>
                                {
                                    error
                                        ? error
                                        : result ? result : text
                                }
                            </label>
                            <input
                                type="text"
                                className="form-control"
                                id="exampleInputEmail1"
                                aria-describedby="emailHelp"
                                value={value}
                                onChange={event => setValue(event.target.value)}
                            />
                            <div id="emailHelp" className="form-text">Example: 15 usd in eur</div>
                        </div>
                        <button type="submit" className="btn btn-primary">Exchange</button>
                    </form>
                </div>
            </div>
        </div>
    )
}

export default CalcPage