import React, {useEffect, useState} from 'react'
import {NavLink} from 'react-router-dom'
import {fetchCurrencies} from "api";

const Header = () => {
    const [dateUpdate, setDateUpdate] = useState('')

    useEffect(async () => {
        const dateFetch = await fetchCurrencies().then(res => res.data.Date)
        const date = dateFetch.slice(0, 10)
        setDateUpdate(date)
    })

    return (
        <nav className="navbar navbar-expand-xs navbar-light bg-light">
            <div className="container-fluid">
                <div className="collapse navbar-collapse" id="navbarNav">
                    <ul className="navbar-nav">
                        <li className="nav-item">
                            <NavLink exact to='/' className="nav-link">Currencies</NavLink>
                        </li>
                        <li className="nav-item">
                            <NavLink to='/calc' className="nav-link">Currency calculator</NavLink>
                        </li>
                    </ul>
                    {dateUpdate && (
                        <button type="button" className="btn btn-primary" style={{"marginLeft": "40px"}}>
                            Ð¡urrency exchange rates update <span className="badge bg-secondary">{dateUpdate}</span>
                        </button>
                    )}
                </div>
            </div>
        </nav>
    )
}

export default Header